exports.buildFromFile = function (file) {
  // read config:
  let fileContents;
  try {
    fileContents = fs.readFileSync(file, 'utf-8');
  } catch (e) {
    console.error("Could not read specified file");
    console.error(e);
    return;
  }

  // parse file with config:
  const pipelineConfig = datapullPipeline.parse(fileContents);
  if (!pipelineConfig) {
    console.error("Could not parse pipeline config");
    return;
  }

  let pipelines;
  try {
    pipelines = datapullPipeline.build(pipelineConfig);
  } catch (e) {
    console.error("Could not build the pipeline");
    console.error(e);
    return;
  }

  return pipelines;
};
